namespace TalentoPlus_S.A.S.ll.Web.Areas.Admin.Models
{
    /// <summary>
    /// DTO para desnormalizar datos del Excel independientemente del orden de columnas
    /// Permite importar archivos con columnas en cualquier orden
    /// </summary>
    public class DatosDesnormalizados
    {
        // Información Personal
        public string Nombres { get; set; } = string.Empty;
        public string Apellidos { get; set; } = string.Empty;
        public string Email { get; set; } = string.Empty;
        public string Telefono { get; set; } = string.Empty;
        public string Direccion { get; set; } = string.Empty;

        // Fechas
        public string FechaNacimiento { get; set; } = string.Empty;
        public string FechaIngreso { get; set; } = string.Empty;

        // Información Laboral
        public string Cargo { get; set; } = string.Empty;
        public string Salario { get; set; } = string.Empty;
        public string Estado { get; set; } = string.Empty;
        public string NivelEducativo { get; set; } = string.Empty;
        public string Departamento { get; set; } = string.Empty;

        // Opcional
        public string? PerfilProfesional { get; set; }

        // Metadata
        public int NumeroFila { get; set; }
        public bool TieneDatos => !string.IsNullOrWhiteSpace(Email) || !string.IsNullOrWhiteSpace(Nombres);
    }
}

namespace TalentoPlus_S.A.S.ll.Web.Areas.Admin.Models
{
    /// <summary>
    /// Resultado de la importación masiva de empleados desde Excel
    /// </summary>
    public class ImportResultado
    {
        public bool Exitoso { get; set; }
        public int TotalFilas { get; set; }
        public int Importados { get; set; }
        public int Actualizados { get; set; }
        public int Fallidos { get; set; }
        public List<string> Errores { get; set; } = new();
        public List<string> Advertencias { get; set; } = new();
        public List<EmpleadoImportado> EmpleadosImportados { get; set; } = new();
        public DateTime FechaImportacion { get; set; } = DateTime.Now;
        public string? NombreArchivo { get; set; }
    }

    /// <summary>
    /// Información resumida de un empleado importado
    /// </summary>
    public class EmpleadoImportado
    {
        public int? Id { get; set; }
        public string NombreCompleto { get; set; } = string.Empty;
        public string Email { get; set; } = string.Empty;
        public string Cargo { get; set; } = string.Empty;
        public string Departamento { get; set; } = string.Empty;
        public string Estado { get; set; } = string.Empty;
        public bool EsActualizacion { get; set; }
    }
}

namespace TalentoPlus_S.A.S.ll.Web.Areas.Admin.Models
{
    /// <summary>
    /// Configuración para importación masiva de empleados
    /// Permite personalizar el comportamiento de la importación
    /// </summary>
    public class ImportacionMasiva
    {
        /// <summary>
        /// Actualizar empleados existentes si se encuentran por email
        /// </summary>
        public bool ActualizarExistentes { get; set; } = true;

        /// <summary>
        /// Detener la importación al primer error
        /// </summary>
        public bool DetenerEnError { get; set; } = false;

        /// <summary>
        /// Validar emails duplicados antes de importar
        /// </summary>
        public bool ValidarDuplicados { get; set; } = true;

        /// <summary>
        /// Enviar notificación por email al completar
        /// </summary>
        public bool NotificarAlCompletar { get; set; } = false;

        /// <summary>
        /// Número máximo de filas a procesar (0 = sin límite)
        /// </summary>
        public int MaximoFilas { get; set; } = 0;

        /// <summary>
        /// Columna que identifica al empleado (para actualizaciones)
        /// </summary>
        public string ColumnaIdentificador { get; set; } = "Email";

        /// <summary>
        /// Mapeo personalizado de columnas (nombre en Excel -> nombre esperado)
        /// </summary>
        public Dictionary<string, string> MapeoColumnas { get; set; } = new();

        /// <summary>
        /// Fila donde comienzan los datos (por defecto fila 2, después de headers)
        /// </summary>
        public int FilaInicioDatos { get; set; } = 2;

        /// <summary>
        /// Nombre del archivo siendo importado
        /// </summary>
        public string? NombreArchivo { get; set; }

        /// <summary>
        /// Validaciones adicionales personalizadas
        /// </summary>
        public List<string> ValidacionesAdicionales { get; set; } = new();
    }
}

namespace TalentoPlus_S.A.S.ll.Web.Areas.Admin.Models
{
    /// <summary>
    /// Mapeo dinámico de columnas del Excel
    /// Detecta automáticamente el orden de las columnas basándose en los headers
    /// </summary>
    public class MapeoColumnasExcel
    {
        // Índices de columnas (base 1, como en Excel)
        public int? ColumnaNombres { get; set; }
        public int? ColumnaApellidos { get; set; }
        public int? ColumnaEmail { get; set; }
        public int? ColumnaTelefono { get; set; }
        public int? ColumnaDireccion { get; set; }
        public int? ColumnaFechaNacimiento { get; set; }
        public int? ColumnaFechaIngreso { get; set; }
        public int? ColumnaCargo { get; set; }
        public int? ColumnaSalario { get; set; }
        public int? ColumnaEstado { get; set; }
        public int? ColumnaNivelEducativo { get; set; }
        public int? ColumnaDepartamento { get; set; }
        public int? ColumnaPerfilProfesional { get; set; }

        /// <summary>
        /// Valida que todas las columnas requeridas estén mapeadas
        /// </summary>
        public bool EsValido()
        {
            return ColumnaNombres.HasValue
                && ColumnaApellidos.HasValue
                && ColumnaEmail.HasValue
                && ColumnaTelefono.HasValue
                && ColumnaDireccion.HasValue
                && ColumnaFechaNacimiento.HasValue
                && ColumnaFechaIngreso.HasValue
                && ColumnaCargo.HasValue
                && ColumnaSalario.HasValue
                && ColumnaEstado.HasValue
                && ColumnaNivelEducativo.HasValue
                && ColumnaDepartamento.HasValue;
            // ColumnaPerfilProfesional es opcional
        }

        /// <summary>
        /// Obtiene lista de columnas faltantes
        /// </summary>
        public List<string> ObtenerColumnasFaltantes()
        {
            var faltantes = new List<string>();

            if (!ColumnaNombres.HasValue) faltantes.Add("Nombres");
            if (!ColumnaApellidos.HasValue) faltantes.Add("Apellidos");
            if (!ColumnaEmail.HasValue) faltantes.Add("Email");
            if (!ColumnaTelefono.HasValue) faltantes.Add("Teléfono");
            if (!ColumnaDireccion.HasValue) faltantes.Add("Dirección");
            if (!ColumnaFechaNacimiento.HasValue) faltantes.Add("Fecha de Nacimiento");
            if (!ColumnaFechaIngreso.HasValue) faltantes.Add("Fecha de Ingreso");
            if (!ColumnaCargo.HasValue) faltantes.Add("Cargo");
            if (!ColumnaSalario.HasValue) faltantes.Add("Salario");
            if (!ColumnaEstado.HasValue) faltantes.Add("Estado");
            if (!ColumnaNivelEducativo.HasValue) faltantes.Add("Nivel Educativo");
            if (!ColumnaDepartamento.HasValue) faltantes.Add("Departamento");

            return faltantes;
        }

        /// <summary>
        /// Crea el mapeo desde los headers de Excel
        /// Detecta automáticamente el orden de las columnas
        /// </summary>
        public static MapeoColumnasExcel CrearDesdeHeaders(List<string> headers)
        {
            var mapeo = new MapeoColumnasExcel();

            for (int i = 0; i < headers.Count; i++)
            {
                var header = NormalizarHeader(headers[i]);
                var columna = i + 1; // Excel usa base 1

                if (CoincideHeader(header, "nombres", "nombre", "first name", "firstname"))
                    mapeo.ColumnaNombres = columna;
                else if (CoincideHeader(header, "apellidos", "apellido", "last name", "lastname"))
                    mapeo.ColumnaApellidos = columna;
                else if (CoincideHeader(header, "email", "correo", "correo electronico", "e-mail", "mail"))
                    mapeo.ColumnaEmail = columna;
                else if (CoincideHeader(header, "telefono", "teléfono", "phone", "celular", "movil"))
                    mapeo.ColumnaTelefono = columna;
                else if (CoincideHeader(header, "direccion", "dirección", "address", "domicilio"))
                    mapeo.ColumnaDireccion = columna;
                else if (CoincideHeader(header, "fecha de nacimiento", "fecha nacimiento", "nacimiento", "birth date", "birthdate"))
                    mapeo.ColumnaFechaNacimiento = columna;
                else if (CoincideHeader(header, "fecha de ingreso", "fecha ingreso", "ingreso", "hire date", "hiredate"))
                    mapeo.ColumnaFechaIngreso = columna;
                else if (CoincideHeader(header, "cargo", "position", "puesto", "rol", "posicion"))
                    mapeo.ColumnaCargo = columna;
                else if (CoincideHeader(header, "salario", "salary", "sueldo", "remuneracion", "pago"))
                    mapeo.ColumnaSalario = columna;
                else if (CoincideHeader(header, "estado", "status", "state", "estatus"))
                    mapeo.ColumnaEstado = columna;
                else if (CoincideHeader(header, "nivel educativo", "educacion", "education level", "education", "nivel"))
                    mapeo.ColumnaNivelEducativo = columna;
                else if (CoincideHeader(header, "departamento", "department", "area", "área", "seccion"))
                    mapeo.ColumnaDepartamento = columna;
                else if (CoincideHeader(header, "perfil profesional", "perfil", "profile", "professional profile", "descripcion"))
                    mapeo.ColumnaPerfilProfesional = columna;
            }

            return mapeo;
        }

        private static string NormalizarHeader(string header)
        {
            return header.ToLower().Trim()
                .Replace("á", "a").Replace("é", "e").Replace("í", "i")
                .Replace("ó", "o").Replace("ú", "u").Replace("ñ", "n");
        }

        private static bool CoincideHeader(string header, params string[] patrones)
        {
            return patrones.Any(p => header.Contains(NormalizarHeader(p)));
        }
    }
}

using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using TalentoPlus_S.A.S.ll.Web.Interfaces;
using TalentoPlus_S.A.S.ll.Web.Models;

namespace TalentoPlus_S.A.S.ll.Web.Controllers;

[Authorize]
public class DepartamentosController : Controller
{
    private readonly IDepartamentoRepository _departamentoRepository;

    public DepartamentosController(IDepartamentoRepository departamentoRepository)
    {
        _departamentoRepository = departamentoRepository;
    }

    // GET: Departamentos
    public async Task<IActionResult> Index()
    {
        var departamentos = await _departamentoRepository.GetDepartamentosConEmpleadosAsync();
        return View(departamentos);
    }

    // GET: Departamentos/Details/5
    public async Task<IActionResult> Details(int? id)
    {
        if (id == null)
        {
            return NotFound();
        }

        var departamento = await _departamentoRepository.GetByIdAsync(id.Value);

        if (departamento == null)
        {
            return NotFound();
        }

        var cantidad = await _departamentoRepository.GetCantidadEmpleadosByDepartamentoAsync(id.Value);
        ViewBag.CantidadEmpleados = cantidad;

        return View(departamento);
    }

    // GET: Departamentos/Create
    public IActionResult Create()
    {
        return View();
    }

    // POST: Departamentos/Create
    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> Create([Bind("Nombre")] Departamento departamento)
    {
        if (ModelState.IsValid)
        {
            // Validar que no exista un departamento con el mismo nombre
            var existente = await _departamentoRepository.GetByNombreAsync(departamento.Nombre);

            if (existente != null)
            {
                ModelState.AddModelError("Nombre", "Ya existe un departamento con este nombre.");
                return View(departamento);
            }

            await _departamentoRepository.AddAsync(departamento);
            await _departamentoRepository.SaveChangesAsync();
            TempData["SuccessMessage"] = "Departamento creado exitosamente.";
            return RedirectToAction(nameof(Index));
        }

        return View(departamento);
    }

    // GET: Departamentos/Edit/5
    public async Task<IActionResult> Edit(int? id)
    {
        if (id == null)
        {
            return NotFound();
        }

        var departamento = await _departamentoRepository.GetByIdAsync(id.Value);

        if (departamento == null)
        {
            return NotFound();
        }

        return View(departamento);
    }

    // POST: Departamentos/Edit/5
    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> Edit(int id, [Bind("Id,Nombre")] Departamento departamento)
    {
        if (id != departamento.Id)
        {
            return NotFound();
        }

        if (ModelState.IsValid)
        {
            try
            {
                await _departamentoRepository.UpdateAsync(departamento);
                await _departamentoRepository.SaveChangesAsync();
                TempData["SuccessMessage"] = "Departamento actualizado exitosamente.";
                return RedirectToAction(nameof(Index));
            }
            catch (Exception)
            {
                if (!await _departamentoRepository.ExistsAsync(departamento.Id))
                {
                    return NotFound();
                }
                else
                {
                    throw;
                }
            }
        }

        return View(departamento);
    }

    // GET: Departamentos/Delete/5
    public async Task<IActionResult> Delete(int? id)
    {
        if (id == null)
        {
            return NotFound();
        }

        var departamento = await _departamentoRepository.GetByIdAsync(id.Value);

        if (departamento == null)
        {
            return NotFound();
        }

        var cantidad = await _departamentoRepository.GetCantidadEmpleadosByDepartamentoAsync(id.Value);

        if (cantidad > 0)
        {
            TempData["ErrorMessage"] = $"No se puede eliminar el departamento porque tiene {cantidad} empleado(s) asignado(s).";
            return RedirectToAction(nameof(Index));
        }

        return View(departamento);
    }

    // POST: Departamentos/Delete/5
    [HttpPost, ActionName("Delete")]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> DeleteConfirmed(int id)
    {
        var departamento = await _departamentoRepository.GetByIdAsync(id);

        if (departamento != null)
        {
            var cantidad = await _departamentoRepository.GetCantidadEmpleadosByDepartamentoAsync(id);

            if (cantidad > 0)
            {
                TempData["ErrorMessage"] = $"No se puede eliminar el departamento porque tiene {cantidad} empleado(s) asignado(s).";
                return RedirectToAction(nameof(Index));
            }

            await _departamentoRepository.DeleteAsync(departamento);
            await _departamentoRepository.SaveChangesAsync();
            TempData["SuccessMessage"] = "Departamento eliminado exitosamente.";
        }

        return RedirectToAction(nameof(Index));
    }
}

using Microsoft.AspNetCore.Authorization;
using Microsoft.AspNetCore.Mvc;
using Microsoft.AspNetCore.Mvc.Rendering;
using Microsoft.EntityFrameworkCore;
using TalentoPlus_S.A.S.ll.Web.Data;
using TalentoPlus_S.A.S.ll.Web.Models;

namespace TalentoPlus_S.A.S.ll.Web.Controllers;

[Authorize]
public class EmpleadosController : Controller
{
    private readonly ApplicationDbContext _context;

    public EmpleadosController(ApplicationDbContext context)
    {
        _context = context;
    }

    // GET: Empleados
    public async Task<IActionResult> Index(string? searchString, int? departamentoId, EstadoEmpleado? estado)
    {
        ViewData["CurrentFilter"] = searchString;
        ViewData["DepartamentoId"] = departamentoId;
        ViewData["Estado"] = estado;

        ViewData["Departamentos"] = new SelectList(await _context.Departamentos.ToListAsync(), "Id", "Nombre");
        ViewData["Estados"] = new SelectList(Enum.GetValues(typeof(EstadoEmpleado)));

        var empleados = _context.Empleados.Include(e => e.Departamento).AsQueryable();

        if (!string.IsNullOrEmpty(searchString))
        {
            empleados = empleados.Where(e => 
                e.Nombres.Contains(searchString) || 
                e.Apellidos.Contains(searchString) ||
                e.Email.Contains(searchString) ||
                e.Cargo.Contains(searchString));
        }

        if (departamentoId.HasValue)
        {
            empleados = empleados.Where(e => e.DepartamentoId == departamentoId.Value);
        }

        if (estado.HasValue)
        {
            empleados = empleados.Where(e => e.Estado == estado.Value);
        }

        return View(await empleados.OrderBy(e => e.Apellidos).ToListAsync());
    }

    // GET: Empleados/Details/5
    public async Task<IActionResult> Details(int? id)
    {
        if (id == null)
        {
            return NotFound();
        }

        var empleado = await _context.Empleados
            .Include(e => e.Departamento)
            .FirstOrDefaultAsync(m => m.Id == id);

        if (empleado == null)
        {
            return NotFound();
        }

        return View(empleado);
    }

    // GET: Empleados/Create
    public async Task<IActionResult> Create()
    {
        ViewData["DepartamentoId"] = new SelectList(await _context.Departamentos.ToListAsync(), "Id", "Nombre");
        ViewData["NivelesEducativos"] = new SelectList(Enum.GetValues(typeof(NivelEducativo)));
        ViewData["Estados"] = new SelectList(Enum.GetValues(typeof(EstadoEmpleado)));
        return View();
    }

    // POST: Empleados/Create
    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> Create([Bind("Nombres,Apellidos,FechaNacimiento,Direccion,Telefono,Email,Cargo,Salario,FechaIngreso,Estado,NivelEducativo,PerfilProfesional,DepartamentoId")] Empleado empleado)
    {
        if (ModelState.IsValid)
        {
            _context.Add(empleado);
            await _context.SaveChangesAsync();
            TempData["SuccessMessage"] = "Empleado creado exitosamente.";
            return RedirectToAction(nameof(Index));
        }

        ViewData["DepartamentoId"] = new SelectList(await _context.Departamentos.ToListAsync(), "Id", "Nombre", empleado.DepartamentoId);
        ViewData["NivelesEducativos"] = new SelectList(Enum.GetValues(typeof(NivelEducativo)), empleado.NivelEducativo);
        ViewData["Estados"] = new SelectList(Enum.GetValues(typeof(EstadoEmpleado)), empleado.Estado);
        return View(empleado);
    }

    // GET: Empleados/Edit/5
    public async Task<IActionResult> Edit(int? id)
    {
        if (id == null)
        {
            return NotFound();
        }

        var empleado = await _context.Empleados.FindAsync(id);
        if (empleado == null)
        {
            return NotFound();
        }

        ViewData["DepartamentoId"] = new SelectList(await _context.Departamentos.ToListAsync(), "Id", "Nombre", empleado.DepartamentoId);
        ViewData["NivelesEducativos"] = new SelectList(Enum.GetValues(typeof(NivelEducativo)), empleado.NivelEducativo);
        ViewData["Estados"] = new SelectList(Enum.GetValues(typeof(EstadoEmpleado)), empleado.Estado);
        return View(empleado);
    }

    // POST: Empleados/Edit/5
    [HttpPost]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> Edit(int id, [Bind("Id,Nombres,Apellidos,FechaNacimiento,Direccion,Telefono,Email,Cargo,Salario,FechaIngreso,Estado,NivelEducativo,PerfilProfesional,DepartamentoId")] Empleado empleado)
    {
        if (id != empleado.Id)
        {
            return NotFound();
        }

        if (ModelState.IsValid)
        {
            try
            {
                _context.Update(empleado);
                await _context.SaveChangesAsync();
                TempData["SuccessMessage"] = "Empleado actualizado exitosamente.";
            }
            catch (DbUpdateConcurrencyException)
            {
                if (!EmpleadoExists(empleado.Id))
                {
                    return NotFound();
                }
                else
                {
                    throw;
                }
            }
            return RedirectToAction(nameof(Index));
        }

        ViewData["DepartamentoId"] = new SelectList(await _context.Departamentos.ToListAsync(), "Id", "Nombre", empleado.DepartamentoId);
        ViewData["NivelesEducativos"] = new SelectList(Enum.GetValues(typeof(NivelEducativo)), empleado.NivelEducativo);
        ViewData["Estados"] = new SelectList(Enum.GetValues(typeof(EstadoEmpleado)), empleado.Estado);
        return View(empleado);
    }

    // GET: Empleados/Delete/5
    public async Task<IActionResult> Delete(int? id)
    {
        if (id == null)
        {
            return NotFound();
        }

        var empleado = await _context.Empleados
            .Include(e => e.Departamento)
            .FirstOrDefaultAsync(m => m.Id == id);

        if (empleado == null)
        {
            return NotFound();
        }

        return View(empleado);
    }

    // POST: Empleados/Delete/5
    [HttpPost, ActionName("Delete")]
    [ValidateAntiForgeryToken]
    public async Task<IActionResult> DeleteConfirmed(int id)
    {
        var empleado = await _context.Empleados.FindAsync(id);
        if (empleado != null)
        {
            _context.Empleados.Remove(empleado);
            await _context.SaveChangesAsync();
            TempData["SuccessMessage"] = "Empleado eliminado exitosamente.";
        }

        return RedirectToAction(nameof(Index));
    }

    private bool EmpleadoExists(int id)
    {
        return _context.Empleados.Any(e => e.Id == id);
    }
}

