namespace TalentoPlus_S.A.S.ll.Web.Repositories;

public class DepartamentoRepository : GenericRepository<Departamento>, IDepartamentoRepository
{
    public DepartamentoRepository(ApplicationDbContext context) : base(context)
    {
    }

    public async Task<Departamento?> GetByNombreAsync(string nombre)
    {
        return await _dbSet
            .FirstOrDefaultAsync(d => d.Nombre == nombre);
    }

    public async Task<IEnumerable<Departamento>> GetDepartamentosConEmpleadosAsync()
    {
        return await _dbSet
            .Include(d => d.Empleados)
            .OrderBy(d => d.Nombre)
            .ToListAsync();
    }

    public async Task<int> GetCantidadEmpleadosByDepartamentoAsync(int departamentoId)
    {
        var departamento = await _dbSet
            .Include(d => d.Empleados)
            .FirstOrDefaultAsync(d => d.Id == departamentoId);

        return departamento?.Empleados.Count ?? 0;
    }
}

using Microsoft.EntityFrameworkCore;
using TalentoPlus_S.A.S.ll.Web.Data;
using TalentoPlus_S.A.S.ll.Web.Data.Entities;

namespace TalentoPlus_S.A.S.ll.Web.Repositories;

public class EmpleadoRepository : GenericRepository<Empleado>, IEmpleadoRepository
{
    public EmpleadoRepository(ApplicationDbContext context) : base(context)
    {
    }

    public async Task<IEnumerable<Empleado>> GetAllWithDepartamentosAsync()
    {
        return await _dbSet
            .Include(e => e.Departamento)
            .OrderBy(e => e.Apellidos)
            .ThenBy(e => e.Nombres)
            .ToListAsync();
    }

    public async Task<Empleado?> GetByIdWithDepartamentoAsync(int id)
    {
        return await _dbSet
            .Include(e => e.Departamento)
            .FirstOrDefaultAsync(e => e.Id == id);
    }

    public async Task<IEnumerable<Empleado>> SearchAsync(
        string? searchString, 
        int? departamentoId, 
        EstadoEmpleado? estado)
    {
        var query = _dbSet.Include(e => e.Departamento).AsQueryable();

        if (!string.IsNullOrWhiteSpace(searchString))
        {
            query = query.Where(e => 
                e.Nombres.Contains(searchString) ||
                e.Apellidos.Contains(searchString) ||
                e.Email.Contains(searchString) ||
                e.Cargo.Contains(searchString));
        }

        if (departamentoId.HasValue)
        {
            query = query.Where(e => e.DepartamentoId == departamentoId.Value);
        }

        if (estado.HasValue)
        {
            query = query.Where(e => e.Estado == estado.Value);
        }

        return await query
            .OrderBy(e => e.Apellidos)
            .ThenBy(e => e.Nombres)
            .ToListAsync();
    }

    public async Task<IEnumerable<Empleado>> GetByDepartamentoAsync(int departamentoId)
    {
        return await _dbSet
            .Include(e => e.Departamento)
            .Where(e => e.DepartamentoId == departamentoId)
            .OrderBy(e => e.Apellidos)
            .ToListAsync();
    }

    public async Task<IEnumerable<Empleado>> GetByEstadoAsync(EstadoEmpleado estado)
    {
        return await _dbSet
            .Include(e => e.Departamento)
            .Where(e => e.Estado == estado)
            .OrderBy(e => e.Apellidos)
            .ToListAsync();
    }

    public async Task<bool> EmailExistsAsync(string email, int? excludeId = null)
    {
        var query = _dbSet.AsQueryable();

        if (excludeId.HasValue)
        {
            query = query.Where(e => e.Id != excludeId.Value);
        }

        return await query.AnyAsync(e => e.Email == email);
    }
}

