using TalentoPlus_S.A.S.ll.Web.Interfaces;

namespace TalentoPlus_S.A.S.ll.Web.Repositories;

/// <summary>
/// Implementación genérica del repositorio base
/// </summary>
/// <typeparam name="T">Tipo de entidad</typeparam>
public class Repository<T> : IRepository<T> where T : class
{
    protected readonly ApplicationDbContext _context;
    protected readonly DbSet<T> _dbSet;

    public Repository(ApplicationDbContext context)
    {
        _context = context;
        _dbSet = context.Set<T>();
    }

    public virtual async Task<IEnumerable<T>> GetAllAsync()
    {
        return await _dbSet.ToListAsync();
    }

    public virtual async Task<T?> GetByIdAsync(int id)
    {
        return await _dbSet.FindAsync(id);
    }

    public virtual async Task<T> AddAsync(T entity)
    {
        await _dbSet.AddAsync(entity);
        return entity;
    }

    public virtual async Task UpdateAsync(T entity)
    {
        _dbSet.Update(entity);
        await Task.CompletedTask;
    }

    public virtual async Task DeleteAsync(T entity)
    {
        _dbSet.Remove(entity);
        await Task.CompletedTask;
    }

    public virtual async Task<bool> ExistsAsync(int id)
    {
        var entity = await GetByIdAsync(id);
        return entity != null;
    }

    public virtual async Task<int> SaveChangesAsync()
    {
        return await _context.SaveChangesAsync();
    }
}

using TalentoPlus_S.A.S.ll.Web.Data.Entities;
using TalentoPlus_S.A.S.ll.Web.Repositories;

namespace TalentoPlus_S.A.S.ll.Web.Services;

public class DepartamentoService : IDepartamentoService
{
    private readonly IDepartamentoRepository _departamentoRepository;

    public DepartamentoService(IDepartamentoRepository departamentoRepository)
    {
        _departamentoRepository = departamentoRepository;
    }

    public async Task<IEnumerable<Departamento>> GetAllDepartamentosAsync()
    {
        return await _departamentoRepository.GetDepartamentosConEmpleadosAsync();
    }

    public async Task<Departamento?> GetDepartamentoByIdAsync(int id)
    {
        return await _departamentoRepository.GetByIdAsync(id);
    }

    public async Task<Departamento> CreateDepartamentoAsync(Departamento departamento)
    {
        // Validaciones de negocio
        if (await NombreExistsAsync(departamento.Nombre))
        {
            throw new InvalidOperationException("Ya existe un departamento con este nombre.");
        }

        await _departamentoRepository.AddAsync(departamento);
        await _departamentoRepository.SaveChangesAsync();
        return departamento;
    }

    public async Task UpdateDepartamentoAsync(Departamento departamento)
    {
        await _departamentoRepository.UpdateAsync(departamento);
        await _departamentoRepository.SaveChangesAsync();
    }

    public async Task DeleteDepartamentoAsync(int id)
    {
        var departamento = await _departamentoRepository.GetByIdAsync(id);
        if (departamento == null)
        {
            throw new KeyNotFoundException($"No se encontró el departamento con ID {id}");
        }

        // Validar que no tenga empleados asignados
        var cantidadEmpleados = await GetCantidadEmpleadosAsync(id);
        if (cantidadEmpleados > 0)
        {
            throw new InvalidOperationException($"No se puede eliminar el departamento porque tiene {cantidadEmpleados} empleado(s) asignado(s).");
        }

        await _departamentoRepository.DeleteAsync(departamento);
        await _departamentoRepository.SaveChangesAsync();
    }

    public async Task<bool> NombreExistsAsync(string nombre)
    {
        var departamento = await _departamentoRepository.GetByNombreAsync(nombre);
        return departamento != null;
    }

    public async Task<int> GetCantidadEmpleadosAsync(int departamentoId)
    {
        return await _departamentoRepository.GetCantidadEmpleadosByDepartamentoAsync(departamentoId);
    }
}

using TalentoPlus_S.A.S.ll.Web.Data.Entities;
using TalentoPlus_S.A.S.ll.Web.Repositories;

namespace TalentoPlus_S.A.S.ll.Web.Services;

public class EmpleadoService : IEmpleadoService
{
    private readonly IEmpleadoRepository _empleadoRepository;

    public EmpleadoService(IEmpleadoRepository empleadoRepository)
    {
        _empleadoRepository = empleadoRepository;
    }

    public async Task<IEnumerable<Empleado>> GetAllEmpleadosAsync()
    {
        return await _empleadoRepository.GetAllWithDepartamentosAsync();
    }

    public async Task<Empleado?> GetEmpleadoByIdAsync(int id)
    {
        return await _empleadoRepository.GetByIdWithDepartamentoAsync(id);
    }

    public async Task<IEnumerable<Empleado>> SearchEmpleadosAsync(
        string? searchString, 
        int? departamentoId, 
        EstadoEmpleado? estado)
    {
        return await _empleadoRepository.SearchAsync(searchString, departamentoId, estado);
    }

    public async Task<Empleado> CreateEmpleadoAsync(Empleado empleado)
    {
        // Validaciones de negocio
        if (await _empleadoRepository.EmailExistsAsync(empleado.Email))
        {
            throw new InvalidOperationException("Ya existe un empleado con este email.");
        }

        await _empleadoRepository.AddAsync(empleado);
        await _empleadoRepository.SaveChangesAsync();
        return empleado;
    }

    public async Task UpdateEmpleadoAsync(Empleado empleado)
    {
        // Validaciones de negocio
        if (await _empleadoRepository.EmailExistsAsync(empleado.Email, empleado.Id))
        {
            throw new InvalidOperationException("Ya existe otro empleado con este email.");
        }

        await _empleadoRepository.UpdateAsync(empleado);
        await _empleadoRepository.SaveChangesAsync();
    }

    public async Task DeleteEmpleadoAsync(int id)
    {
        var empleado = await _empleadoRepository.GetByIdAsync(id);
        if (empleado == null)
        {
            throw new KeyNotFoundException($"No se encontró el empleado con ID {id}");
        }

        await _empleadoRepository.DeleteAsync(empleado);
        await _empleadoRepository.SaveChangesAsync();
    }

    public async Task<bool> EmailExistsAsync(string email, int? excludeId = null)
    {
        return await _empleadoRepository.EmailExistsAsync(email, excludeId);
    }
}

